function error_display(a){$.each(a,function(a,b){return show_label_error(a,b),void $("#"+a).focus()})}function show_label_error(a,b){0==$("label[for='"+a+"'] #"+a+"-error").length?$("label[for='"+a+"']").append(" <span id=\""+a+"-error\" class=\"error\"> ( "+b+")</span>"):$("#"+a+"-error").html(b).show()}function formReset(){$("input[type='text'],input[type='password'], input[type='email'],input[type='hidden'],input[type='file'], textarea").val("")}function block_form(){show_loader(),$("button[type=submit]").attr("disabled","disabled").addClass("disable-btn")}function error_remove(){hide_loader(),$("button[type=submit]").removeAttr("disabled").removeClass("disable-btn"),$("label span.error").remove()}$(document).ready(function(){$("[data-toggle=\"tooltip\"]").tooltip(),$("body").on("mouseenter",".custom-tooltip",function(a){a.preventDefault(),$(this).popover("show")}),$("body").on("mouseleave",".custom-tooltip",function(a){a.preventDefault(),$(this).popover("hide")})});function show_loader(){$("#form-loader").show()}function hide_loader(){$("#form-loader").hide()}function display_toster(a,b=1,c=5e3){1==b?toastr.success(a,"Success!",{timeOut:c}):toastr.error(a,"Error!",{timeOut:c})};

$(function(){
	$('body').on('keypress', '.allow_only_number', function (evt) {
        const txt = $(this).val();
        const charCode = (evt.which) ? evt.which : evt.keyCode;
          if (charCode == 46) {
            //Check if the text already contains the . character
            if (txt.indexOf('.') === -1) {
              return true;
            } else {
              return false;
            }
          } else {
            if (charCode > 31 &&
              (charCode < 48 || charCode > 57))
              return false;
          }
          return true;
    });

    $('body').on('keypress', '.allow_only_integer', function (evt) {
        var regex = new RegExp("^[0-9]+$");
        var str = String.fromCharCode(!evt.charCode ? evt.which : evt.charCode);
        if (regex.test(str)) {
            return true;
        }

        evt.preventDefault();
        return false;
    });
});